import { COLOR_BLACK, COLOR_BLUE } from './colors';

export const BOX_SHADOW_PRIMARY = `0px 0px 10px 3px transparent, 0px 0px 10px ${COLOR_BLACK}`;
export const BOX_SHADOW_PRIMARY_HOVER = `0px 0px 10px 3px ${COLOR_BLUE}, 0px 0px 10px ${COLOR_BLACK}`;
