import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import PropTypes from 'prop-types';


const Header = styled.header`
  display: flex;
  align-items: center;
  flex-direction: column;
  width: 100%;
  margin-bottom: 3rem;
  text-align: center;
`;

const Back = styled.div`
  align-self: flex-start;
  margin-bottom: 1.5rem;

  a {
    font-family: inherit;
    font-size: 1.6rem;
    display: flex;
    align-items: center;
  }
`;

const Title = styled.h2`
  font-family: 'Star Jedi', 'Open Sans', sans-serif;
  font-size: 4rem;
  margin-bottom: 1.5rem;
  letter-spacing: 2px;
`;

const Team = styled.p`
  font-family: inherit;

  &:not(:last-child) {
    margin-bottom: 1rem;
  }
`;

export default function MovieDetailHeader({
  title,
  director,
  producer,
}: MovieDetailHeaderProps) {
  return (
    <Header>
      <Back>
        <Link to='/'>&larr; Back to list</Link>
      </Back>
      <Title>{title}</Title>
      <Team>Director : {director}</Team>
      <Team>Producer : {producer}</Team>
    </Header>
  );
}

export type MovieDetailHeaderProps = {
  title: string | null;
  director: string | null;
  producer: string | null;
};

MovieDetailHeader.propTypes = {
  title: PropTypes.string,
  director: PropTypes.string,
  producer: PropTypes.string,
};
